$(document).ready(function(){
    if($('#usersTable')[0]){
        getUsers();
    }
});

function getUsers(){          
    $('#usersTable').DataTable({
        processing: true,
        pageLength: 30,
        responsive: true,
        serverSide: true,
        ajax: $(location).attr('protocol')+'//'+$(location).attr('host')+'/home',
        dom: 'Bfrtip',
        buttons: ['copy', 'csv', 'excel', 'pdf', 'print'],
        columns: [
            {data: 'id'},
            {data: 'name'},
            {data: 'email'},
            {data: 'created_at'},
            {data: 'updated_at'}
        ]
        
    });
}
